

var LongNum = "";
LongNum = LongNum.replace(" ",""); //remvoe spaces
//document.writeln(LongNum);

// loop within string.
// get 13 character string
// if  0 is within string, find location of 0 and get next string
// else calculate string of 13
// repeat

function calculateProduct ( s) { //calculate product of characters in string
    var val = 1;
    for (var j=0;j< s.length;j++) {
        val*= parseInt(s.substr(j,1));
    }
    return (val);
}

var HighestProduct = 0;
var tempShortString = "";
var IndexOf0 = -1;
var stringProduct = 0;
for (var i=0;i<(LongNum.length-12);i++) {
    //document.writeln (LongNum.charAt(i)); 
    tempShortString = LongNum.substr(i,13); //get ShortString

    IndexOf0 = tempShortString.lastIndexOf("0");
    
    if (IndexOf0 > -1  ) { //0 found ignore string, find next available string from  last 0 found
        i+=IndexOf0;
    }
    else {//0 not found
        stringProduct = calculateProduct(tempShortString);
        if (HighestProduct < stringProduct)
            HighestProduct=stringProduct;
//document.writeln(i.toString() +  ". " + tempShortString + "<br>");
    }
}
document.writeln("Highest Product: " + HighestProduct.toString());